import io
import os
import zipfile

import click
import requests

# %% ../notebooks/06_data_downloading.ipynb 5
CONTEXT_SETTINGS = dict(help_option_names=['-h', '--help'],
                        token_normalize_func=lambda x: x.lower() if isinstance(x, str) else x # can run with --COUNT or --count, --duck or --dUcK etc..
                        )

@click.command(context_settings=CONTEXT_SETTINGS)
@click.option('-o', '--output_dir', required=False, help='location of output directory')
def get_sample_data(url="https://lobsterdata.com/info/sample/LOBSTER_SampleFile_AMZN_2012-06-21_5.zip",
                    output_dir=None):
    """Download and extract sample data from LOBSTER website for AMZN."""
    if output_dir is None:
        output_dir = os.path.join(os.getcwd(), "data/AMZN_2012-06-21_2-2012-06-21_5")

    os.makedirs(output_dir, exist_ok=True)
    
    response = requests.get(url)
    if response.status_code != 200:
        print(f"Failed to download data. HTTP Status Code: {response.status_code}")
        return
    
    with zipfile.ZipFile(io.BytesIO(response.content)) as zip_ref:
        zip_ref.extractall(output_dir)

